------------------- DATABASE---------------------
CREATE TABLE NHANVIEN ( 
  MANV CHAR(5) PRIMARY KEY,
  TENNV VARCHAR2(50),
  PHAI VARCHAR2(5),
  NGAYSINH DATE,
  DIACHI VARCHAR2(100),
  SODT VARCHAR2(100),
  LUONG NUMBER(10,2),
  PHUCAP NUMBER(10,2),
  VAITRO VARCHAR2(20),
  MANQL CHAR(5),
  PHG CHAR(5)
);
ALTER TABLE NHANVIEN
ADD FOREIGN KEY (MANQL) REFERENCES NHANVIEN(MANV);

CREATE TABLE PHONGBAN (
  MAPB CHAR(5) PRIMARY KEY,
  TENPB VARCHAR2(50),
  TRPHG CHAR(5),
  CONSTRAINT FK_PB_NV FOREIGN KEY (TRPHG) REFERENCES NHANVIEN(MANV)
);

CREATE TABLE DEAN (
  MADA CHAR(5) PRIMARY KEY,
  TENDA VARCHAR2(50) UNIQUE,
  NGAYBD DATE,
  PHONG CHAR(5),
  FOREIGN KEY (PHONG) REFERENCES PHONGBAN(MAPB)
);

CREATE TABLE PHANCONG (
  MANV CHAR(5) NOT NULL,
  MADA CHAR(5) NOT NULL,
  THOIGIAN DATE,
  PRIMARY KEY (MANV, MADA),
  FOREIGN KEY (MANV) REFERENCES NHANVIEN(MANV),
  FOREIGN KEY (MADA) REFERENCES DEAN(MADA)
);
ALTER TABLE NHANVIEN
ADD CONSTRAINT FK_NV_PB FOREIGN KEY (PHG) REFERENCES PHONGBAN(MAPB);

