CONN ADMIN01/1;

CREATE OR REPLACE PACKAGE p_key IS
    pk nchar(15):='123456789123456';
    Procedure set_key(new_key nchar);
  FUNCTION get_key RETURN nchar;
END;
/

CREATE OR REPLACE PACKAGE BODY p_key IS
    Procedure set_key( new_key nchar) is
    begin
    pk:=new_key;
    end;
  FUNCTION get_key RETURN nchar IS
  BEGIN
    RETURN p_key.pk;
  END;
END;
/
CONN ADMIN01/1
set serveroutput on;
DECLARE
  my_value nchar(15);
BEGIN
    --p_key.set_key(new_key =>'123456789123455' ); 
    my_value := p_key.get_key(); -- G?i hàm get_my_var và l?u giá tr? tr? v? vào bi?n my_value
    DBMS_OUTPUT.PUT_LINE(my_value); -- In giá tr? c?a bi?n my_value ra màn hình
END;
/
CONN ADMIN01/1;
ALTER TABLE NHANVIEN
ADD LUONG_C VARCHAR2(100);
ALTER TABLE NHANVIEN
ADD  PHUCAP_C VARCHAR2(100);
UPDATE NHANVIEN
SET LUONG_C =DBMS_CRYPTO.encrypt(UTL_RAW.CAST_TO_RAW (LUONG), 4353 /* = dbms_crypto.DES_CBC_PKCS5 */, p_key.get_key());
/
UPDATE NHANVIEN
SET PHUCAP_C =DBMS_CRYPTO.encrypt(UTL_RAW.CAST_TO_RAW (PHUCAP), 4353 /* = dbms_crypto.DES_CBC_PKCS5 */,  p_key.get_key());

