CREATE VIEW VIEW_NHANVIEN_NS AS 
SELECT MANV,TENNV,PHAI,TO_CHAR(NGAYSINH, 'YYYY-MM-DD') as NGAYSINH,DIACHI,SODT, CASE WHEN MANV = (select USER FROM DUAL) THEN UTL_RAW.CAST_TO_varchar2(DBMS_CRYPTO.decrypt(LUONG, 4353,  p_key.get_key()))  ELSE NULL END AS LUONG, CASE WHEN MANV = (select USER FROM DUAL) THEN UTL_RAW.CAST_TO_varchar2(DBMS_CRYPTO.decrypt(PHUCAP, 4353,  p_key.get_key())) ELSE NULL END AS PHUCAP, VAITRO,MANQL,PHG
FROM NHANVIEN;
/
----Trigger update du lieu thay doi o view vao bang goc
CONN ADMIN01/1
CREATE OR REPLACE TRIGGER trg_sync_nhanvien
INSTEAD OF INSERT OR UPDATE ON VIEW_NHANVIEN_NS
FOR EACH ROW
DECLARE
    new_luong varchar2(100);
    new_phucap varchar2(100);
BEGIN
    if(:new.luong!=:old.luong) then
    new_luong:= DBMS_CRYPTO.encrypt(UTL_RAW.CAST_TO_RAW (:new.LUONG), 4353 /* = dbms_crypto.DES_CBC_PKCS5 */, p_key.get_key());
    else
    new_luong:=:old.luong;
    end if;
    if(:new.phucap!=:old.phucap) then
    new_phucap:= DBMS_CRYPTO.encrypt(UTL_RAW.CAST_TO_RAW (:new.PHUCAP), 4353 /* = dbms_crypto.DES_CBC_PKCS5 */, p_key.get_key());
    else
    new_phucap:=:old.phucap;
    end if;
  IF INSERTING THEN
    INSERT INTO NHANVIEN (MANV, TENNV, PHAI, NGAYSINH,DIACHI,
    SODT,LUONG, PHUCAP, VAITRO,MANQL,PHG)
    VALUES (:NEW.MANV,:NEW.TENNV,:NEW.PHAI,:NEW.NGAYSINH,:NEW.DIACHI,:NEW.SODT, new_luong, new_phucap,:NEW.VAITRO, :NEW.MANQL, :NEW.PHG);
  ELSIF UPDATING THEN
    UPDATE NHANVIEN
    SET TENNV = :NEW.TENNV,PHAI=:NEW.PHAI,NGAYSINH=:NEW.NGAYSINH,DIACHI=:NEW.DIACHI,SODT=:NEW.SODT,
    LUONG=new_luong,
    PHUCAP=new_phucap,
        VAITRO=:NEW.VAITRO, MANQL=:NEW.MANQL, PHG=:NEW.PHG
    WHERE MANV = :NEW.MANV;
  END IF;
END;
/
