CREATE OR REPLACE FUNCTION SELECT_PHANCONG (p_schema varchar2, p_obj varchar2)
RETURN varchar2
AS
    USER1 VARCHAR2(100);
    VT VARCHAR2(100);
    MAPHONGBAN CHAR(5);
BEGIN
    select USER into USER1 FROM DUAL;
    select VAITRO into VT from NHANVIEN where MANV = USER1;
    IF ( VT = 'QL truc tiep' ) THEN
        RETURN 'MANV='''||USER1||''' or MANV in (select MANV from ADMIN01.vw_QLTRUCTIEP_XemThongTinNhanVien where MANQL = '''||USER1||''' )'; 
     ELSIF ( VT = 'Truong phong' ) THEN
        SELECT PHG INTO MAPHONGBAN FROM  NHANVIEN WHERE NHANVIEN.MANV = USER1;
        RETURN 'PHANCONG.MANV in (select MANV from ADMIN01.vw_TRUONGPHONG_Xem_NHANVIEN where PHG = '''||MAPHONGBAN||''' )';
    ELSIF ( VT = 'Ban giam doc' or VT =  'Tai chinh' ) THEN
        RETURN '1=1';
    else 
        return 'MANV = '''||USER1||'''';   
    END IF;
END;
/   
BEGIN DBMS_RLS.DROP_POLICY
    (object_schema =>'ADMIN01',
    object_name =>'PHANCONG',
    policy_name => 'XemPHANCONGtheoROLE');
END;
/
BEGIN DBMS_RLS.ADD_POLICY
    (object_schema =>'ADMIN01',
    object_name =>'PHANCONG',
    policy_name => 'XemPHANCONGtheoROLE',
    policy_function => 'SELECT_PHANCONG',
    statement_types => 'SELECT, UPDATE, DELETE, INSERT',
    update_check => TRUE);
END;
